mapper.insert:
INSERT INTO CO_DOMAIN (
    DOMAIN_CD,        DOMAIN_NM,         DOMAIN_URL
)
VALUES (
    #{domainCd},      #{domainNm},       #{domainUrl}
)

mapper.update:
UPDATE CO_DOMAIN 
   SET
       DOMAIN_NM  = #{domainNm},
       DOMAIN_URL = #{domainUrl}
 WHERE DOMAIN_CD = #{domainCd}
